function ShowPopup(n){var t=$("<div/>");$.get(n).done(function(n){t.html(n);popup=t.dialog({autoOpen:!0,resizeable:!1,width:600,height:400,title:"Add or Edit Data",close:function(){popup.dialog("destroy").remove()}})})}function SubmitAddEdit(n){if($.validator.unobtrusive.parse(n),$(n).valid()){var t=$(n).serializeJSON();t=JSON.stringify(t);$.ajax({type:"POST",url:"/api/todo",data:t,contentType:"application/json",success:function(n){n.success&&(popup.dialog("close"),ShowMessage(n.message),dataTable.ajax.reload())}})}return!1}function Delete(n){swal({title:"Are you sure want to Delete?",text:"You will not be able to restore the file!",type:"warning",showCancelButton:!0,confirmButtonColor:"#DD6B55",confirmButtonText:"Yes, delete it!",closeOnConfirm:!0},function(){$.ajax({type:"DELETE",url:"/api/todo/"+n,success:function(n){n.success&&(ShowMessage(n.message),dataTable.ajax.reload())}})})}function ShowMessage(n){toastr.success(n)}var popup,dataTable;$(document).ready(function(){dataTable=$("#gridTodo").DataTable({pageLength:5,lengthChange:!1,processing:!0,serverSide:!0,ajax:{url:"/api/todo",type:"GET",datatype:"json"},columns:[{data:"description"},{data:"createdBy"},{data:"estimatedDuration"},{data:"id",render:function(n){return"<a class='btn btn-default btn-sm' onclick=ShowPopup('/Home/AddEditTodo/"+n+"')><i class='fa fa-pencil'><\/i> Edit<\/a><a class='btn btn-danger btn-sm' style='margin-left:5px' onclick=Delete("+n+")><i class='fa fa-trash'><\/i> Delete<\/a>"}}],language:{emptyTable:"no data found."},initComplete:function(){this.api().columns().every(function(){var n=this,t=$('<input type="text"/>').appendTo($(n.footer()).empty()).on("change",function(){var t=$.fn.dataTable.util.escapeRegex($(this).val());n.search(t,!0,!1).draw()});n.data().unique().sort().each(function(n){t.append('<option value="'+n+'">'+n+"<\/option>")})})}})});